import React, { Component, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Slider from "react-slick";
import { getMostPopular } from "../../redux/actions";
import Card from "../Card/Card";
import "./Carousel.css"
import "./slick.css";
import "./slick-theme.css";

export default function SimpleSlider() {

    const dispatch = useDispatch()
    const popular = useSelector(state => state.mostPopular)

    useEffect(() => {
        dispatch(getMostPopular())
    }, [dispatch]);

    popular.results && popular.results.map(e => {
        console.log(e.background_image)
        
    })

    var settings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        pauseOnHover: true,
    };

return (
    <Slider {...settings}>
        <div>
            {popular.results && popular.results.map(image => {
                return (
                    <Card key={image.id} image={image.background_image}/>
                )
            })}
        </div>
    </Slider>
);
  }
