import React, { Component, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Slider from "react-slick";
import { getMostPopular } from "../../redux/actions";
import "./slick.css";
import "./slick-theme.css";

export default function SimpleSlider() {

    const dispatch = useDispatch()
    const popular = useSelector(state => state.mostPopular)

    useEffect(() => {
        dispatch(getMostPopular())
    }, [dispatch]);

    console.log(popular.results.background_image)
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 5000,
    //   centerMode: true,
    };
    return (
      <Slider {...settings}>
        <div>
          {/* {popular && popular.results.map(image => {
            return (
                <img src={image.background_image} alt='img not found'/>
            )
          })} */}
        </div>
      </Slider>
    );
  }
