import React, { Component, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Slider from "react-slick";
import { getMostPopular } from "../../redux/actions";
import "./Carousel.css"
import "./slick.css";
import "./slick-theme.css";

export default function SimpleSlider({image}) {

    const dispatch = useDispatch()
    const popular = useSelector(state => state.mostPopular)

    useEffect(() => {
        dispatch(getMostPopular())
        console.log(mostPopular.id)
    }, [dispatch]);

    let mostPopular = popular.results && popular.results.map(e => e)


    var settings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        pauseOnHover: true,
    };

return (
    <Slider {...settings}>
        <div className="carousel">
           <img className="img-popular" src={image} alt="img not found"/>
        </div>
    </Slider>
);
  }
