import React, { useState } from 'react';
import './Pagination.css'

export default function Pagination({ page, setPage, max }) {

    const [input, setInput] = useState(1);

    const nextPage = () => {
        setInput(input + 1)
        setPage(page + 1)
    };

    const previousPage = () => {
        setInput(input - 1);
        setPage(page - 1)
    };

    const onKeyDown = (e) => {
        if (e.keyCode == 13) {
            setPage(parseInt(e.target.value))

            if (parseInt(e.target.value < 1) || 
            parseInt(e.target.value) > Math.ceil(max) || 
            isNaN(parseInt(e.target.value))) {
            }
        }
    }

    return (
        <div className='pagination'>
            <button onClick={previousPage}>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="btn">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 19.5L8.25 12l7.5-7.5" />
                </svg>
            </button>
            <input name='page' autoComplete='off' type='text' value={input} />
            <p>1 of  {page}</p>
            <button onClick={nextPage}>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="btn">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M8.25 4.5l7.5 7.5-7.5 7.5" />
                </svg>
            </button>
        </div>
    )
}
