const initialState = {
    videogames: [],
    allGenero: [],
    detail: [],
    allVideogames: []
};

function rootReducer(state = initialState, action) {
    switch(action.type) {
        case "GET_VIDEOGAMES":
            return {
                ...state,
                videogames: action.payload,
                allVideogames: action.payload,
            }
        case "SEARCH_BY_NAME":
            return {
                ...state,
                videogames: action.payload,
            }
        case "GET_DETAIL":
            return {
                ...state,
                detail: action.payload,
            }
    }
};

export default rootReducer;
