import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVideogamesDetail } from '../../redux/actions';
import './VideogameDetail.css'


export default function VideogameDetail(props) {

  const dispatch = useDispatch();
  const gamesDetail = useSelector((state) => state.detail);

  const id = props.match.params.id

  useEffect(() => {
    dispatch(getVideogamesDetail(id))
  }, [dispatch, id])

  return (
    <div className='detail-container container'>
      <div>
        <img src={gamesDetail.image} alt='img not found' />
      </div>

      <div className='text-box'>
        <h3 className='heading-tertiary'>{gamesDetail.name} #{gamesDetail.id}</h3>
        <ul className='list'>
          <li><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
            <path strokeLinecap="round" strokeLinejoin="round" d="M6.75 3v2.25M17.25 3v2.25M3 18.75V7.5a2.25 2.25 0 012.25-2.25h13.5A2.25 2.25 0 0121 7.5v11.25m-18 0A2.25 2.25 0 005.25 21h13.5A2.25 2.25 0 0021 18.75m-18 0v-7.5A2.25 2.25 0 015.25 9h13.5A2.25 2.25 0 0121 11.25v7.5m-9-6h.008v.008H12v-.008zM12 15h.008v.008H12V15zm0 2.25h.008v.008H12v-.008zM9.75 15h.008v.008H9.75V15zm0 2.25h.008v.008H9.75v-.008zM7.5 15h.008v.008H7.5V15zm0 2.25h.008v.008H7.5v-.008zm6.75-4.5h.008v.008h-.008v-.008zm0 2.25h.008v.008h-.008V15zm0 2.25h.008v.008h-.008v-.008zm2.25-4.5h.008v.008H16.5v-.008zm0 2.25h.008v.008H16.5V15z" />
          </svg>
            {gamesDetail.released}</li>
          <li>{gamesDetail.rating}</li>
          <li>{gamesDetail.platforms?.join(', ')}</li>
          <li>{gamesDetail.genres?.join(' - ')}</li>
        </ul>

      </div>
      <div dangerouslySetInnerHTML={{ __html: gamesDetail.description }} />
    </div>
  )
};
