import React, { Component, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getMostPopular } from "../../redux/actions";
import { useNavigate } from "react-router-dom";
import Slider from "react-slick";
import "./Carousel.css"
import "./slick.css";
import "./slick-theme.css";

export default function SimpleSlider({ image }) {

    const dispatch = useDispatch();
    const history = useNavigate();
    const popular = useSelector(state => state.mostPopular)

    useEffect(() => {
        dispatch(getMostPopular())
    }, [dispatch]);

    let mostPopular = popular.results && popular.results.map(e => e)


    var settings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        pauseOnHover: true,
    };

    return (
        <Slider {...settings}>
            {popular.results && popular.results.map((img, index) => {
                return (
                    <div key={index}>
                       <img onClick={() => history(`/videogames/${img.id}`)} className="img-popular" src={img.background_image} alt='not found'/>
                        </div>
                )
            })}
        </Slider>
    );
}
