import React, { Component, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Slider from "react-slick";
import { getMostPopular } from "../../redux/actions";
import "./Carousel.css"
import "./slick.css";
import "./slick-theme.css";

export default function SimpleSlider() {

    const dispatch = useDispatch()
    const popular = useSelector(state => state.mostPopular)

    useEffect(() => {
        dispatch(getMostPopular())
    }, [dispatch]);

    popular.results && popular.results.map(e => {
        console.log(e.background_image)
        return null
    })

    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 5000,
    //   centerMode: true,
    };
    return (
      <Slider {...settings}>
        <div>
          {popular.results && popular.results.map(image => {
            return (
                <img className="img-popular" src={image.background_image} alt='img not found'/>
            )
          })}
        </div>
      </Slider>
    );
  }
